Insert.RuleBody:
        import it.cnr.isti.labsedc.glimpse.event.GlimpseBaseEventChoreos;
        import it.cnr.isti.labsedc.glimpse.manager.ResponseDispatcher;
        import it.cnr.isti.labsedc.glimpse.utils.NotifierUtils;
        import it.cnr.isti.labsedc.glimpse.impl.ServiceLocatorParseViolationReceivedFromBSM;
		import it.cnr.isti.labsedc.glimpse.impl.RuleTemplateEnum;

        declare GlimpseBaseEventChoreos
	        @role( event )
	        @timestamp( timeStamp )
        end

		rule "metaRule_INFRASTRUCTUREVIOLATION"
		no-loop true
		salience 20
		dialect "java"
		when
			$aEvent : GlimpseBaseEventChoreos(this.isConsumed == false, this.isException == false, this.getEventName == "SLA Alert");
		then
			$aEvent.setConsumed(true);
			update($aEvent);

			ServiceLocatorParseViolationReceivedFromBSM.GetMachineIP("BSM","a","b",RuleTemplateEnum.INFRASTRUCTUREVIOLATION, (String)$aEvent.getEventData(), $aEvent.getTimeStamp());
			ServiceLocatorParseViolationReceivedFromBSM.GetMachineIP("BSM","a","b",RuleTemplateEnum.NOEVENTFROMINFRASTRUCTURE, (String)$aEvent.getEventData(), $aEvent.getTimeStamp());
			ResponseDispatcher.NotifyMeValue(drools.getRule().getName(), "2", "3", "4");
		end
